/*CREATE    TABLE   Student(  id             int,        name       nchar(10)     NOT  NULL  ,    -- 设置非空约束，学生姓名不能为空  age          smallint,       cardID     nchar(10)    )*//*CREATE    TABLE   Student(  id             int,        name       nchar(10) ,      --设置CHECK约束，学生年龄必须是0~100之间  age          smallint       CHECK  (age>0 AND age<=100) ,       cardID     nchar(10)    )*/CREATE    TABLE   Student(  id             int,        name       nchar(10)     DEFAULT (N'未录入') ,    --默认值约束，学生姓名默认为“未录入”  age          smallint  ,       cardID     nchar(10)    )